import React, {useState, useEffect} from 'react'

let Form = () => {

    const [nums, setNums] = useState()
    const [date, setDate] = useState()



   useEffect((e) => {
       console.log(nums)
   })

    let style

    if (/^[0-9]{16}$/.test(nums)) {
        console.log('Valid card number')
        style = {
            border: '1px solid #00ff35'
        }
    }
    else if (!/^[0-9]{16}$/.test(nums)) {
        console.log('Invalid card number')
        style = {
            border: '1px solid #ff004b'
        }
    }

    if (/^[0-9]{4}$/.test(date)) {
        console.log('Valid card date')
        style = {
            border: '1px solid #00ff35'
        }
    }
    else if (!/^[0-9]{4}$/.test(date)){
        console.log('Invalid card date')
        style = {
            border: '1px solid #ff004b'
        }
    }


    return (
        <div className = 'form'>
            <input style={style} className = 'cardNumber' value = {nums} type = 'text' placeholder = 'Card number' maxLength = '16' onChange={(event) => setNums(event.target.value)}/>
            <input className = 'cardDate' type = 'text' placeholder = 'MM/YY' maxLength = '4' onChange={(event) => setDate(event.target.value)}/>
            <input className = 'cardCVV' type = 'text' placeholder = 'CVV2' maxLength = '3' />
        </div>
    )
}

export default Form
