let Form = () => {
    const [text, setText] = useState()

   useEffect((e) => {
       console.log(text)
   })

    let regEx = /[0-9]{16}$/

    if (regEx.test(text)) {
        console.log(true)
    }
    else  console.log(false)



    return (
        <div className = 'form'>
            <input className = 'cardNumber' value = {text} type = 'text' placeholder = 'Card number' onChange={(event) => setText(event.target.value)}/>
            <input type="text" name="month" placeholder="MM" maxLength="2" size="2"/>
            <span>/</span>
            <input type="text" name="year" placeholder="YY" maxLength="2" size="2"/>
            <input className = 'cardCVV' type = 'text' placeholder = 'CVV2'/>
        </div>
    )
}

export default Form
